import styled from 'styled-components';
import { darken } from 'polished';

export const Container = styled.div`
  max-width: 600px;
  margin: 50px auto;
  display: flex;
  justify-content: center;
  flex-direction: column;

  header {
    display: flex;
    align-self: center;
    align-items: center;

    button {
      margin: 5px 0 0;
      height: 44px;
      background: none;
      font-weight: bold;
      color: #fff;
      border: 0;
      border-radius: 4px;
      font-size: 16px;
      transition: background 0.2s;

      &:hover {
        background: ${darken(0.01, '#f25')};
      }
    }
  }
`;
